function get_recipes($ingredient){

  $recipe_set = get_ingredient_recipes($ingredient);

  $recipe_count = count($recipe_set);
  $html = '';

  for($i = 1; $i < $recipe_count; $i++){
  	$html = $html . '<p>' . $recipe_set[$i] . '</p>';
  }

  return $html;

}

function get_ingredient_recipes($ingredient){
  global $ingredient_recipes;

  $lc_ingredient = strtolower($ingredient);
  $lc_ingredient = str_replace(' ', '', $lc_ingredient);

  $ingrep_count = count($ingredient_recipes);

  for( $i = 0; $i < $ingrep_count; $i++ ) {
  	if( $ingredient_recipes[$i][0] == $lc_ingredient ){
  	  return $ingredient_recipes[$i];
  	}
  }
}
